        <p class="text-gray-600 dark:text-gray-300">Clique para enviar arquivo .xlsx</p>
        <input 
          ref="fileInput" 
          type="file" 
          accept=".xlsx, .xls, .csv" 
          class="hidden" 
          @change="handleFileChange"
