        <div v-for="field in collectionFields" :key="field.id" class="grid grid-cols-2 gap-4 items-center">
          <div class="text-sm font-medium">{{ field.name }} <span v-if="field.required" class="text-red-500">*</span></div>
          <USelect 
            v-model="mapping[field.id]" 
            :options="fileHeaders" 
            placeholder="Selecione Coluna Excel"
          />
        </div>
      </div>
      
      <div class="mt-4">
        <h4 class="font-bold text-sm mb-2">Pr-visualizao (Primeira Linha)</h4>
        <div class="bg-gray-100 dark:bg-gray-800 p-2 rounded text-xs overflow-x-auto">
          <pre>{{ previewRow }}</pre>
        </div>
      </div>
    </div>

    <!-- Step 3: Processing -->
    <div v-if="step === 3" class="text-center py-8">
      <UIcon name="i-heroicons-arrow-path" class="w-12 h-12 animate-spin text-primary-500 mx-auto mb-4" />
      <p>Processando {{ fullData.length }} itens...</p>
    </div>

    <!-- Actions -->
    <div class="flex justify-end gap-3 mt-6">
      <UButton v-if="step > 1 && step < 3" color="gray" variant="ghost" @click="step = 1">Voltar</UButton>
      <UButton v-if="step === 2" @click="processImport">Importar Linhas</UButton>
    </div>
  </div>
</template>

<script setup lang="ts">
import { read, utils } from 'xlsx'

const props = defineProps<{
  collectionFields: any[]
  collectionId: string
}>()

const emit = defineEmits(['close', 'refresh'])

const step = ref(1)
const fileInput = ref<HTMLInputElement | null>(null)
const fileHeaders = ref<string[]>([])
const fullData = ref<any[]>([])
const mapping = ref<Record<string, string>>({}) // FieldID -> ExcelHeader

const triggerFileInput = () => {
  fileInput.value?.click()
}

const handleFileChange = async (e: Event) => {
  const file = (e.target as HTMLInputElement).files?.[0]
  if (!file) return

  const data = await file.arrayBuffer()
  const workbook = read(data)
  const sheet = workbook.Sheets[workbook.SheetNames[0]]
  const json = utils.sheet_to_json(sheet)

  if (json.length === 0) {
    alert('Empty file')
    return
  }

  fullData.value = json
  fileHeaders.value = Object.keys(json[0] as object)
  
  // Auto-map if names match
  props.collectionFields.forEach(f => {
    const match = fileHeaders.value.find(h => h.toLowerCase() === f.name.toLowerCase())
    if (match) mapping.value[f.id] = match
  })

  step.value = 2
}

const previewRow = computed(() => {
  if (fullData.value.length === 0) return {}
  return fullData.value[0]
})

const processImport = async () => {
  step.value = 3
  const supabase = useSupabaseClient()
  const toast = useToast()

  try {
    const itemsToInsert: any[] = []
    const valuesToInsert: any[] = []

    // 1. Create Items
    // We can't batch insert items easily and get IDs back in a way that maps 1:1 reliably without specific order guarantees.
    // However, Supabase insert returns rows. If we insert N items, we get N rows.
    
    // Identify required fields
    const requiredFields = props.collectionFields.filter(f => f.required).map(f => f.id)
    let skippedCount = 0

    // Filter data before processing
    // We need to map keys first to check values against field IDs
    const validRows: any[] = []

    fullData.value.forEach((row) => {
      let isValid = true
      
      // Check each required field
      for (const fieldId of requiredFields) {
        const header = mapping.value[fieldId]
        const value = row[header]
        
        // Check if empty (undefined, null, or empty string)
        // Numeric 0 is valid. False is valid.
        if (value === undefined || value === null || value === '') {
          isValid = false
          break
        }
      }

      if (isValid) {
        validRows.push(row)
      } else {
        skippedCount++
      }
    })

    const batchSize = 50
    const chunks = []
    for (let i = 0; i < validRows.length; i += batchSize) {
      chunks.push(validRows.slice(i, i + batchSize))
    }

    for (const chunk of chunks) {
      // 1. Insert Items
      const { data: createdItems, error: itemError } = await supabase
        .from('items')
        .insert(chunk.map(() => ({ collection_id: props.collectionId })))
        .select()
      
      if (itemError) throw itemError

      // 2. Prepare Values
      createdItems.forEach((item, index) => {
        const rowData = chunk[index]
        Object.entries(mapping.value).forEach(([fieldId, excelHeader]) => {
          const value = rowData[excelHeader as keyof typeof rowData]
          if (value !== undefined && value !== null && value !== '') {
            valuesToInsert.push({
              item_id: item.id,
              field_id: fieldId,
              value: value 
            })
          }
        })
      })
    }

    // 3. Insert Values Batch
    if (valuesToInsert.length > 0) {
       const { error: valError } = await supabase
         .from('item_values')
         .insert(valuesToInsert)
       
       if (valError) throw valError
    }

    toast.add({ 
      title: 'Success', 
      description: `Imported ${validRows.length} items. Skipped ${skippedCount} items due to missing required fields.`, 
      color: 'green',
      timeout: 5000 
    })
    emit('refresh')
    emit('close')

  } catch (error: any) {
    console.error(error)
    toast.add({ title: 'Error', description: error.message, color: 'red' })
    step.value = 2 // Go back
  }
}
</script>
<template>
  <div class="mt-8">
    <h3 class="text-xl font-bold mb-4">Comentrios</h3>
    
    <!-- Add Comment -->
    <div class="mb-6 flex gap-3" v-if="user">
      <UAvatar :src="user.user_metadata.avatar_url" :alt="user.user_metadata.full_name" />
      <div class="flex-1 space-y-2">
        <UTextarea v-model="newComment" placeholder="Escreva um comentrio..." :rows="2" autoresize />
        <div class="flex justify-end">
          <UButton size="sm" :loading="posting" :disabled="!newComment.trim()" @click="postComment">Comentar</UButton>
        </div>
      </div>
    </div>

    <!-- List -->
    <div class="space-y-4">
      <div v-if="comments.length === 0" class="text-center text-gray-500 py-4">
        Nenhum comentrio ainda. Seja o primeiro!
      </div>
      
      <div v-else v-for="comment in comments" :key="comment.id" class="flex gap-3">
        <UAvatar :src="comment.profiles?.avatar_url" :alt="comment.profiles?.full_name" size="sm" />
        <div class="flex-1">
          <div class="bg-gray-50 dark:bg-gray-800 rounded-lg p-3">
            <div class="flex justify-between items-start mb-1">
              <span class="font-semibold text-sm">{{ comment.profiles?.full_name || 'Annimo' }}</span>
              <span class="text-xs text-gray-500">{{ new Date(comment.created_at).toLocaleDateString() }}</span>
            </div>
            <p class="text-sm text-gray-700 dark:text-gray-300 whitespace-pre-wrap">{{ comment.content }}</p>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script setup lang="ts">
const props = defineProps<{
  collectionId: string
}>()

const supabase = useSupabaseClient()
const user = useSupabaseUser()
const toast = useToast()

const comments = ref<any[]>([])
const newComment = ref('')
const posting = ref(false)

const loadComments = async () => {
  const { data, error } = await supabase
    .from('comments')
    .select(`
      id, 
      content, 
      created_at, 
      profiles ( full_name, avatar_url )
    `)
    .eq('collection_id', props.collectionId)
    .order('created_at', { ascending: false })
  
  if (!error && data) {
    comments.value = data
  }
}

const postComment = async () => {
  if (!newComment.value.trim() || !user.value) return
  
  posting.value = true
  try {
    const { error } = await supabase
      .from('comments')
      .insert({
        collection_id: props.collectionId,
        user_id: user.value.id,
        content: newComment.value.trim()
      })
    
    if (error) throw error
    
    newComment.value = ''
    loadComments()
    
  } catch (error: any) {
    toast.add({ title: 'Erro', description: error.message, color: 'red' })
  } finally {
    posting.value = false
  }
}

onMounted(loadComments)
</script>
<template>
  <div class="p-4">
    <!-- Header -->
    <div class="mb-6 flex flex-col md:flex-row justify-between md:items-center gap-4">
      <div>
        <div class="flex items-center gap-2 mb-1">
           <UButton to="/dashboard" icon="i-heroicons-arrow-left" variant="ghost" color="gray" size="sm" />
           <h1 class="text-2xl font-bold">{{ collection?.name || 'Carregando...' }}</h1>
        </div>
        <p class="text-gray-500 text-sm ml-9">{{ collection?.description }}</p>
      </div>
      <div class="flex gap-2">
        <UButton 
          v-if="selectedItems.length > 0" 
          color="red" 
          variant="soft" 
          icon="i-heroicons-trash" 
          @click="deleteSelectedItems"
        >
          Excluir ({{ selectedItems.length }})
        </UButton>
        <UButton variant="outline" icon="i-heroicons-arrow-up-tray" @click="isOpenImport = true">Importar Excel</UButton>
        <UButton icon="i-heroicons-plus" @click="openCreateModal">Novo Item</UButton>
      </div>
    </div>

    <!-- Filters/Search -->
    <div class="mb-4 flex flex-col md:flex-row gap-2">
      <UInput v-model="searchQuery" icon="i-heroicons-magnifying-glass" placeholder="Buscar em todas as colunas..." class="w-full md:w-64" />
      
      <UPopover>
        <UButton color="white" icon="i-heroicons-funnel" trailing-icon="i-heroicons-chevron-down">
           Filtros {{ activeFiltersCount > 0 ? `(${activeFiltersCount})` : '' }}
        </UButton>
        <template #panel>
          <div class="p-4 w-80 space-y-4">
            <h4 class="font-bold text-sm">Filtros Ativos</h4>
            <div v-for="field in fields" :key="field.id" class="flex flex-col gap-1">
               <label class="text-xs font-medium text-gray-500">{{ field.name }}</label>
               <UInput 
                  v-model="filters[field.id]" 
                  size="xs" 
                  :placeholder="`Filtrar por ${field.name}`" 
               />
            </div>
            <UButton v-if="activeFiltersCount > 0" size="xs" color="gray" variant="ghost" block @click="clearFilters">Limpar Todos</UButton>
          </div>
        </template>
      </UPopover>
    </div>

    <!-- Table -->
    <UTable
      v-model="selectedItems"
      :rows="filteredRows"
      :columns="tableColumns"
      :loading="pending"
      class="w-full"
    >
      <template #actions-data="{ row }">
        <div class="flex gap-1 justify-end">
          <UButton icon="i-heroicons-pencil-square" size="xs" color="gray" variant="ghost" @click="openEditModal(row)" />
          <UButton icon="i-heroicons-trash" size="xs" color="red" variant="ghost" @click="deleteItem(row.id)" />
        </div>
      </template>

      <!-- Custom Cell Rendering for Multiselect/Boolean/Date -->
      <template v-for="col in dynamicColumns" :key="col.key" #[`${col.key}-data`]="{ row }">
         <span v-if="col.type === 'boolean'">
            <UBadge :color="row[col.key] ? 'green' : 'gray'" variant="soft">{{ row[col.key] ? 'Sim' : 'No' }}</UBadge>
         </span>
         <span v-else-if="col.type === 'multiselect'">
            {{ Array.isArray(row[col.key]) ? row[col.key].join(', ') : row[col.key] }}
         </span>
         <span v-else>
            {{ row[col.key] }}
         </span>
      </template>
    </UTable>

    <!-- Create/Edit Modal -->
    <UModal v-model="isOpen">
      <UCard>
        <template #header>
          <div class="font-bold">{{ editingItemId ? 'Editar Item' : 'Novo Item' }}</div>
        </template>

        <ItemForm
          v-if="fields.length > 0"
          :fields="fields"
          :initial-data="editingItemData"
          :loading="saving"
          @save="handleSave"
          @cancel="isOpen = false"
        />
      </UCard>
    </UModal>
    
    <!-- Import Modal -->
    <UModal v-model="isOpenImport">
       <UCard>
         <template #header>Importar do Excel</template>
         <ImportWizard 
            v-if="isOpenImport" 
            :collection-id="collectionId" 
            :collection-fields="fields" 
            @close="isOpenImport = false"
            @refresh="loadData"
         />
       </UCard>
    </UModal>

    <!-- Comments -->
    <CommentSection :collection-id="collectionId" />

  </div>
</template>

<script setup lang="ts">
const route = useRoute()
const collectionId = route.params.id as string
const supabase = useSupabaseClient()
const toast = useToast()

const collection = ref<any>(null)
const fields = ref<any[]>([])
const items = ref<any[]>([])
const pending = ref(true)
const selectedItems = ref<any[]>([])

// Modal State
const isOpen = ref(false)
const isOpenImport = ref(false)
const editingItemId = ref<string | null>(null)
const editingItemData = ref<any>({})
const saving = ref(false)

// Filter State
const searchQuery = ref('')
const filters = ref<Record<string, string>>({})

const activeFiltersCount = computed(() => {
  return Object.values(filters.value).filter(val => val && val.trim() !== '').length
})

const clearFilters = () => {
  filters.value = {}
}

// Load Data
const loadData = async () => {
  pending.value = true
  try {
    // 1. Get Collection & Fields
    const { data: colData, error: colError } = await supabase
      .from('collections')
      .select(`*, fields (*)`)
      .eq('id', collectionId)
      .single()
    
    if (colError) throw colError
    
    collection.value = colData
    // Sort fields by order
    fields.value = (colData.fields || []).sort((a: any, b: any) => a.folder_order - b.folder_order)

    // 2. Get Items & Values
    // Note: Supabase doesn't easily return a pivot table. We fetch Items -> ItemValues
    const { data: itemData, error: itemError } = await supabase
      .from('items')
      .select(`id, created_at, item_values ( field_id, value )`)
      .eq('collection_id', collectionId)
      .order('created_at', { ascending: false })

    if (itemError) throw itemError
    items.value = itemData || []

  } catch (error: any) {
    toast.add({ title: 'Error', description: error.message, color: 'red' })
  } finally {
    pending.value = false
  }
}

onMounted(() => {
  loadData()
})

// Computed Table Rows (Pivot)
const rows = computed(() => {
  return items.value.map(item => {
    const row: any = { id: item.id, created_at: item.created_at }
    item.item_values.forEach((iv: any) => {
      row[iv.field_id] = iv.value
    })
    return row
  })
})

const filteredRows = computed(() => {
  let result = rows.value

  // 1. Search Query
  if (searchQuery.value) {
    const q = searchQuery.value.toLowerCase()
    result = result.filter(row => {
      // Search visible fields mainly
      return Object.values(row).some(val => String(val).toLowerCase().includes(q))
    })
  }

  // 2. Column Filters
  for (const [fieldId, filterValue] of Object.entries(filters.value)) {
    if (filterValue && filterValue.trim() !== '') {
       const q = filterValue.toLowerCase()
       result = result.filter(row => {
          const val = row[fieldId]
          return String(val ?? '').toLowerCase().includes(q)
       })
    }
  }

  return result
})

// Dynamic Columns
const dynamicColumns = computed(() => {
  return fields.value.filter(f => f.visible).map(f => ({
    key: f.id,
    label: f.name,
    sortable: true,
    type: f.type
  }))
})

const tableColumns = computed(() => {
  return [
    ...dynamicColumns.value,
    { key: 'actions', label: 'Aes' }
  ]
})

// Actions
// ...
const handleSave = async (formData: any) => {
// ...
  try{
    toast.add({ title: 'Sucesso', description: 'Item salvo', color: 'green' })
    isOpen.value = false
    loadData() // Reload table

  } catch (error: any) {
    toast.add({ title: 'Erro', description: error.message, color: 'red' })
  } finally {
    saving.value = false
  }
}

const deleteItem = async (id: string) => {
  if (!confirm('Excluir item?')) return
  const { error } = await supabase.from('items').delete().eq('id', id)
  if (error) {
    toast.add({ title: 'Erro', description: error.message, color: 'red' })
  } else {
    toast.add({ title: 'Excludo', color: 'green' })
    items.value = items.value.filter(i => i.id !== id)
  }
}

const deleteSelectedItems = async () => {
  const count = selectedItems.value.length
  if (count === 0) return
  if (!confirm(`Tem certeza que deseja excluir ${count} itens?`)) return

  const ids = selectedItems.value.map(i => i.id)
  
  const { error } = await supabase
    .from('items')
    .delete()
    .in('id', ids)

  if (error) {
    toast.add({ title: 'Erro', description: error.message, color: 'red' })
  } else {
    toast.add({ title: 'Excludo', description: `${count} itens excludos`, color: 'green' })
    items.value = items.value.filter(i => !ids.includes(i.id))
    selectedItems.value = []
  }
}
</script>
