export default defineComponent({
  components: {
    HCombobox,
    HComboboxButton,
    HComboboxOptions,
    HComboboxOption,
    HComboboxInput,
    HListbox,
    HListboxButton,
    HListboxOptions,
    HListboxOption,
    UIcon,
    UAvatar
  },
  inheritAttrs: false,
  props: {
    modelValue: {
      type: [String, Number, Object, Array, Boolean],
      default: ""
    },
    query: {
      type: String,
      default: null
    },
    by: {
      type: String,
      default: void 0
    },
    options: {
      type: Array,
      default: () => []
    },
    id: {
      type: String,
      default: null
    },
    name: {
      type: String,
      default: null
    },
    required: {
      type: Boolean,
      default: false
    },
    icon: {
      type: String,
      default: null
    },
    loadingIcon: {
      type: String,
      default: () => config.default.loadingIcon
    },
    leadingIcon: {
      type: String,
      default: null
    },
    trailingIcon: {
      type: String,
      default: () => config.default.trailingIcon
    },
    trailing: {
      type: Boolean,
      default: false
    },
    leading: {
      type: Boolean,
      default: false
    },
    loading: {
      type: Boolean,
      default: false
    },
    selectedIcon: {
      type: String,
      default: () => configMenu.default.selectedIcon
    },
    disabled: {
      type: Boolean,
      default: false
    },
    multiple: {
      type: Boolean,
      default: false
    },
    searchable: {
      type: [Boolean, Function],
      default: false
    },
    searchablePlaceholder: {
      type: String,
      default: () => configMenu.default.searchablePlaceholder.label
    },
    searchableLazy: {
      type: Boolean,
      default: false
    },
    clearSearchOnClose: {
      type: Boolean,
      default: () => configMenu.default.clearSearchOnClose
    },
    debounce: {
      type: Number,
      default: 200
    },
    creatable: {
      type: Boolean,
      default: false
    },
    showCreateOptionWhen: {
      type: [String, Function],
      default: () => configMenu.default.showCreateOptionWhen
    },
    placeholder: {
      type: String,
      default: null
    },
    padded: {
      type: Boolean,
      default: true
    },
    size: {
      type: String,
      default: null,
      validator(value) {
        return Object.keys(config.size).includes(value);
      }
    },
    color: {
      type: String,
      default: () => config.default.color,
      validator(value) {
        return [...appConfig.ui.colors, ...Object.keys(config.color)].includes(value);
      }
    },
    variant: {
      type: String,
      default: () => config.default.variant,
      validator(value) {
        return [
          ...Object.keys(config.variant),
          ...Object.values(config.color).flatMap((value2) => Object.keys(value2))
        ].includes(value);
      }
    },
    optionAttribute: {
      type: String,
      default: "label"
    },
    valueAttribute: {
      type: String,
      default: null
    },
    searchAttributes: {
      type: Array,
      default: null
    },
    inputTargetForm: {
      type: String,
      default: null
    },
    popper: {
      type: Object,
      default: () => ({})
    },
    selectClass: {
      type: String,
      default: null
    },
    class: {
      type: [String, Object, Array],
      default: () => ""
    },
    ui: {
      type: Object,
      default: () => ({})
    },
    uiMenu: {
      type: Object,
      default: () => ({})
    }
  },
  emits: ["update:modelValue", "update:query", "open", "close", "change"],
  setup(props, { emit, slots }) {
    if (import.meta.dev && props.multiple && !Array.isArray(props.modelValue)) {
      console.warn(`[@nuxt/ui] The USelectMenu components needs to have a modelValue of type Array when using the multiple prop. Got '${typeof props.modelValue}' instead.`, props.modelValue);
    }
    const { ui, attrs } = useUI("select", toRef(props, "ui"), config, toRef(props, "class"));
    const { ui: uiMenu } = useUI("selectMenu", toRef(props, "uiMenu"), configMenu);
    const popper = computed(() => defu({}, props.popper, uiMenu.value.popper));
    const [trigger, container] = usePopper(popper.value);
    const by = computed(() => {
      if (!props.by) return void 0;
      if (typeof props.by === "function") {
        return props.by;
      }
      const key = props.by;
      const hasDot = key.indexOf(".");
      if (hasDot > 0) {
        return (a, z) => {
          return accessor(a, key) === accessor(z, key);
        };
      }
      return key;
    });
    const { size: sizeButtonGroup, rounded } = useInjectButtonGroup({ ui, props });
    const { emitFormBlur, emitFormChange, inputId, color, size: sizeFormGroup, name } = useFormGroup(props, config);
    const size = computed(() => sizeButtonGroup.value ?? sizeFormGroup.value);
    const internalQuery = ref("");
    const query = computed({
      get() {
        return props.query ?? internalQuery.value;
      },
      set(value) {
        internalQuery.value = value;
        emit("update:query", value);
      }
    });
    const selected = computed(() => {
      function compareValues(value1, value2) {
        if (by.value && typeof by.value !== "function" && isObject(value1) && isObject(value2)) {
          return isEqual(value1[by.value], value2[by.value]);
        }
        return isEqual(value1, value2);
      }
      function getValue(value) {
        if (props.valueAttribute) {
          return accessor(value, props.valueAttribute);
        }
        return value;
      }
      if (props.multiple) {
        const modelValue = props.modelValue;
        if (!Array.isArray(modelValue) || !modelValue.length) {
          return [];
        }
        return options.value.filter((option) => {
          const optionValue = getValue(option);
          return modelValue.some((value) => compareValues(value, optionValue));
        });
      }
      return options.value.find((option) => {
        const optionValue = getValue(option);
        return compareValues(optionValue, toRaw(props.modelValue));
      }) ?? props.modelValue;
    });
    const label = computed(() => {
      if (!props.modelValue) return null;
      if (Array.isArray(props.modelValue) && props.modelValue.length) {
        return `${props.modelValue.length} selected`;
      } else if (["string", "number"].includes(typeof props.modelValue)) {
        return props.valueAttribute ? accessor(selected.value, props.optionAttribute) : props.modelValue;
      }
      return accessor(props.modelValue, props.optionAttribute);
    });
    const selectClass = computed(() => {
      const variant = ui.value.color?.[color.value]?.[props.variant] || ui.value.variant[props.variant];
      return twMerge(twJoin(
        ui.value.base,
        uiMenu.value.select,
        rounded.value,
        ui.value.size[size.value],
        ui.value.gap[size.value],
        props.padded ? ui.value.padding[size.value] : "p-0",
        variant?.replaceAll("{color}", color.value),
        (isLeading.value || slots.leading) && ui.value.leading.padding[size.value],
        (isTrailing.value || slots.trailing) && ui.value.trailing.padding[size.value]
      ), props.placeholder && (!props.modelValue || Array.isArray(props.modelValue) && !props.modelValue.length) && ui.value.placeholder, props.selectClass);
    });
    const isLeading = computed(() => {
      return props.icon && props.leading || props.icon && !props.trailing || props.loading && !props.trailing || props.leadingIcon;
    });
    const isTrailing = computed(() => {
      return props.icon && props.trailing || props.loading && props.trailing || props.trailingIcon;
    });
    const leadingIconName = computed(() => {
      if (props.loading) {
        return props.loadingIcon;
      }
      return props.leadingIcon || props.icon;
    });
    const trailingIconName = computed(() => {
      if (props.loading && !isLeading.value) {
        return props.loadingIcon;
      }
      return props.trailingIcon || props.icon;
    });
    const leadingWrapperIconClass = computed(() => {
      return twJoin(
        ui.value.icon.leading.wrapper,
        ui.value.icon.leading.pointer,
        ui.value.icon.leading.padding[size.value]
      );
    });
    const leadingIconClass = computed(() => {
      return twJoin(
        ui.value.icon.base,
        color.value && appConfig.ui.colors.includes(color.value) && ui.value.icon.color.replaceAll("{color}", color.value),
        ui.value.icon.size[size.value],
        props.loading && ui.value.icon.loading
      );
    });
    const trailingWrapperIconClass = computed(() => {
      return twJoin(
        ui.value.icon.trailing.wrapper,
        ui.value.icon.trailing.pointer,
        ui.value.icon.trailing.padding[size.value]
      );
    });
    const trailingIconClass = computed(() => {
      return twJoin(
        ui.value.icon.base,
        color.value && appConfig.ui.colors.includes(color.value) && ui.value.icon.color.replaceAll("{color}", color.value),
        ui.value.icon.size[size.value],
        props.loading && !isLeading.value && ui.value.icon.loading
      );
    });
    const debouncedSearch = props.searchable && typeof props.searchable === "function" ? useDebounceFn(props.searchable, props.debounce) : void 0;
    const options = computedAsync(async () => {
      if (debouncedSearch) {
        return await debouncedSearch(query.value);
      }
      return props.options || [];
    }, [], {
      lazy: props.searchableLazy
    });
    function escapeRegExp(string) {
      return string.replace(/[.*+?^${}()|[\]\\]/g, (match) => `\\${match}`);
    }
    function accessor(obj, key) {
      return get(obj, key);
    }
    function isObject(object) {
      return !Array.isArray(object) && object !== null && typeof object === "object";
    }
    const filteredOptions = computed(() => {
      if (!query.value || debouncedSearch) {
        return options.value;
      }
      const escapedQuery = escapeRegExp(query.value);
      return options.value.filter((option) => {
        return (props.searchAttributes?.length ? props.searchAttributes : [props.optionAttribute]).some((searchAttribute) => {
          if (["string", "number"].includes(typeof option)) {
            return String(option).search(new RegExp(escapedQuery, "i")) !== -1;
          }
          const child = get(option, searchAttribute);
          return child !== null && child !== void 0 && String(child).search(new RegExp(escapedQuery, "i")) !== -1;
        });
      });
    });
    const createOption = computed(() => {
      if (query.value === "") {
        return null;
      }
      if (props.showCreateOptionWhen === "empty" && filteredOptions.value.length) {
        return null;
      }
      if (props.showCreateOptionWhen === "always") {
        const existingOption = filteredOptions.value.find((option) => ["string", "number"].includes(typeof option) ? option === query.value : accessor(option, props.optionAttribute) === query.value);
        if (existingOption) {
          return null;
        }
      }
      if (typeof props.showCreateOptionWhen === "function") {
        if (!props.showCreateOptionWhen(query.value, filteredOptions.value)) {
          return null;
        }
      }
      return ["string", "number"].includes(typeof props.modelValue) ? query.value : { [props.optionAttribute]: query.value };
    });
    function clearOnClose() {
      if (props.clearSearchOnClose) {
        query.value = "";
      }
    }
    watch(container, (value) => {
      if (value) {
        emit("open");
      } else {
        clearOnClose();
        emit("close");
        emitFormBlur();
      }
    });
    function onUpdate(value) {
      if (toRaw(props.modelValue) === value) {
        return;
      }
      emit("update:modelValue", value);
      emit("change", value);
      emitFormChange();
    }
    function onQueryChange(event) {
      query.value = event.target.value;
    }
    provideUseId(() => useId());
    return {
      // eslint-disable-next-line vue/no-dupe-keys
      ui,
      // eslint-disable-next-line vue/no-dupe-keys
      uiMenu,
      attrs,
      // eslint-disable-next-line vue/no-dupe-keys
      name,
      inputId,
      // eslint-disable-next-line vue/no-dupe-keys
      popper,
      trigger,
      container,
      selected,
      label,
      accessor,
      isLeading,
      isTrailing,
      // eslint-disable-next-line vue/no-dupe-keys
      selectClass,
      leadingIconName,
      leadingIconClass,
      leadingWrapperIconClass,
      trailingIconName,
      trailingIconClass,
      trailingWrapperIconClass,
      filteredOptions,
      createOption,
      // eslint-disable-next-line vue/no-dupe-keys
      query,
      onUpdate,
      onQueryChange,
      // eslint-disable-next-line vue/no-dupe-keys
      by
    };
  }
});
</script>
<template>
  <form @submit.prevent="save">
    <div class="space-y-4">
      <div v-for="field in fields" :key="field.id">
        <UFormGroup :label="field.name" :name="field.id">

          <!-- Text -->
          <UInput v-if="field.type === 'text'" v-model="model[field.id]" type="text" />

          <!-- Textarea -->
          <UTextarea v-if="field.type === 'textarea'" v-model="model[field.id]" />

          <!-- Number -->
          <UInput v-if="field.type === 'number'" v-model.number="model[field.id]" type="number" />

          <!-- Date -->
          <UInput v-if="field.type === 'date'" v-model="model[field.id]" type="date" />

          <!-- Boolean -->
          <UToggle v-if="field.type === 'boolean'" v-model="model[field.id]" />

          <!-- Select -->
          <USelect v-if="field.type === 'select'" v-model="model[field.id]" :options="getOptions(field)" creatable />

          <!-- MultiSelect -->
          <USelectMenu v-if="field.type === 'multiselect'" v-model="model[field.id]" :options="getOptions(field)"
            multiple creatable />

        </UFormGroup>
      </div>
    </div>

    <div class="mt-6 flex justify-end gap-3">
      <UButton color="gray" variant="ghost" @click="$emit('cancel')">Cancelar</UButton>
      <UButton type="submit" :loading="loading">Salvar Item</UButton>
    </div>
  </form>
</template>

<script setup lang="ts">
const props = defineProps<{
  fields: any[]
  initialData?: any // Record<field_id, value>
  loading?: boolean
  collectionId: string // Needed for fetching dynamic values
}>()

const emit = defineEmits(['save', 'cancel'])

// Initialize model with explicit keys to ensure reactivity
const model = reactive<Record<string, any>>({})
const dynamicOptions = reactive<Record<string, string[]>>({})

const loadDynamicOptions = async (fieldId: string) => {
  if (dynamicOptions[fieldId]) return // Already loaded
  try {
    const data = await $fetch<string[]>(`/api/collections/${props.collectionId}/fields/${fieldId}/values`)
    dynamicOptions[fieldId] = data
  } catch (e) {
    console.error('Failed to load dynamic options', e)
    dynamicOptions[fieldId] = []
  }
}

const getOptions = (field: any) => {
  if (field.is_dynamic) {
    const opts = dynamicOptions[field.id]
    return Array.isArray(opts) ? opts : []
  }
  return Array.isArray(field.options) ? field.options : []
}

// Populate model from initialData or defaults
watch(() => props.fields, (newFields) => {
  newFields.forEach(f => {
    if (props.initialData && props.initialData[f.id] !== undefined) {
      model[f.id] = props.initialData[f.id]
    } else {
      // Defaults
      if (f.type === 'boolean') model[f.id] = false
      else if (f.type === 'multiselect') model[f.id] = []
      else model[f.id] = null
    }

    // Fetch dynamic options if needed
    if (f.is_dynamic && ['select', 'multiselect'].includes(f.type)) {
      loadDynamicOptions(f.id)
    }
  })
}, { immediate: true, deep: true })

const save = () => {
  emit('save', model)
}
</script>
<template>
  <div class="space-y-4">
    <!-- Step 1: Upload -->
    <div v-if="step === 1">
      <div 
        class="border-2 border-dashed border-gray-300 dark:border-gray-700 rounded-lg p-8 text-center cursor-pointer hover:bg-gray-50 dark:hover:bg-gray-800 transition"
        @click="triggerFileInput"
      >
        <UIcon name="i-heroicons-document-arrow-up" class="w-12 h-12 text-gray-400 mx-auto mb-2" />
        <p class="text-gray-600 dark:text-gray-300">Clique para enviar arquivo .xlsx</p>
        <input 
          ref="fileInput" 
          type="file" 
          accept=".xlsx, .xls, .csv" 
          class="hidden" 
          @change="handleFileChange"
        />
      </div>
    </div>

    <!-- Step 2: Map Columns -->
    <div v-if="step === 2">
      <h3 class="font-bold mb-4">Mapear Colunas</h3>
      <div class="space-y-3 max-h-96 overflow-y-auto">
        <div v-for="field in collectionFields" :key="field.id" class="grid grid-cols-2 gap-4 items-center">
          <div class="text-sm font-medium">{{ field.name }} <span v-if="field.required" class="text-red-500">*</span></div>
          <USelect 
            v-model="mapping[field.id]" 
            :options="fileHeaders" 
            placeholder="Selecione Coluna Excel"
          />
        </div>
      </div>
      
      <div class="mt-4">
        <h4 class="font-bold text-sm mb-2">Pr-visualizao (Primeira Linha)</h4>
        <div class="bg-gray-100 dark:bg-gray-800 p-2 rounded text-xs overflow-x-auto">
          <pre>{{ previewRow }}</pre>
        </div>
      </div>
    </div>

    <!-- Step 3: Processing -->
    <div v-if="step === 3" class="text-center py-8">
      <UIcon name="i-heroicons-arrow-path" class="w-12 h-12 animate-spin text-primary-500 mx-auto mb-4" />
      <p>Processando {{ fullData.length }} itens...</p>
    </div>

    <!-- Actions -->
    <div class="flex justify-end gap-3 mt-6">
      <UButton v-if="step > 1 && step < 3" color="gray" variant="ghost" @click="step = 1">Voltar</UButton>
      <UButton v-if="step === 2" @click="processImport">Importar Linhas</UButton>
    </div>
  </div>
</template>

<script setup lang="ts">
import { read, utils } from 'xlsx'

const props = defineProps<{
  collectionFields: any[]
  collectionId: string
}>()

const emit = defineEmits(['close', 'refresh'])

const step = ref(1)
const fileInput = ref<HTMLInputElement | null>(null)
const fileHeaders = ref<string[]>([])
const fullData = ref<any[]>([])
const mapping = ref<Record<string, string>>({}) // FieldID -> ExcelHeader

const triggerFileInput = () => {
  fileInput.value?.click()
}

const handleFileChange = async (e: Event) => {
  const file = (e.target as HTMLInputElement).files?.[0]
  if (!file) return

  const data = await file.arrayBuffer()
  const workbook = read(data)
  const sheet = workbook.Sheets[workbook.SheetNames[0]]
  const json = utils.sheet_to_json(sheet)

  if (json.length === 0) {
    alert('Empty file')
    return
  }

  fullData.value = json
  fileHeaders.value = Object.keys(json[0] as object)
  
  // Auto-map if names match
  props.collectionFields.forEach(f => {
    const match = fileHeaders.value.find(h => h.toLowerCase() === f.name.toLowerCase())
    if (match) mapping.value[f.id] = match
  })

  step.value = 2
}

const previewRow = computed(() => {
  if (fullData.value.length === 0) return {}
  return fullData.value[0]
})

const processImport = async () => {
  step.value = 3
  const toast = useToast()

  try {
    // Identify required fields
    const requiredFields = props.collectionFields.filter(f => f.required).map(f => f.id)
    let skippedCount = 0
    const validRowsForImport: any[] = []

    fullData.value.forEach((row) => {
      let isValid = true
      
      // Check each required field
      for (const fieldId of requiredFields) {
        const header = mapping.value[fieldId]
        const value = row[header]
        
        if (value === undefined || value === null || value === '') {
          isValid = false
          break
        }
      }

      if (isValid) {
        // Map excel row to our internal values format
        const values: Record<string, any> = {}
        Object.entries(mapping.value).forEach(([fieldId, excelHeader]) => {
          const value = row[excelHeader]
          if (value !== undefined && value !== null && value !== '') {
            values[fieldId] = value
          }
        })
        validRowsForImport.push({ values })
      } else {
        skippedCount++
      }
    })

    // Call our new bulk import API
    await $fetch(`/api/collections/${props.collectionId}/import`, {
      method: 'POST',
      body: {
        items: validRowsForImport
      }
    })

    toast.add({ 
      title: 'Sucesso', 
      description: `Importados ${validRowsForImport.length} itens. Pulados ${skippedCount} itens por falta de campos obrigatrios.`, 
      color: 'green',
      timeout: 5000 
    })
    emit('refresh')
    emit('close')

  } catch (error: any) {
    console.error(error)
    toast.add({ title: 'Erro', description: error.message, color: 'red' })
    step.value = 2 // Go back
  }
}
</script>
<template>
  <div :class="wrapperClass" v-bind="attrs">
    <div :class="borderClass" />

    <template v-if="label || icon || avatar || $slots.default">
      <div :class="containerClass">
        <slot>
          <span v-if="label" :class="ui.label">
            {{ label }}
          </span>
          <UIcon v-else-if="icon" :name="icon" :class="ui.icon.base" />
          <UAvatar v-else-if="avatar" v-bind="{ size: ui.avatar.size, ...avatar }" :class="ui.avatar.base" />
        </slot>
      </div>

      <div :class="borderClass" />
    </template>
  </div>
</template>

<script>
import { toRef, computed, defineComponent } from "vue";
import { twJoin } from "tailwind-merge";
import UIcon from "../elements/Icon.vue";
import UAvatar from "../elements/Avatar.vue";
import { useUI } from "../../composables/useUI";
import { mergeConfig, twMerge } from "../../utils";
import appConfig from "#build/app.config";
import { divider } from "#ui/ui.config";
const config = mergeConfig(appConfig.ui.strategy, appConfig.ui.divider, divider);
export default defineComponent({
  components: {
    UIcon,
    UAvatar
  },
  inheritAttrs: false,
  props: {
    label: {
      type: String,
      default: null
    },
    icon: {
      type: String,
      default: null
    },
    avatar: {
      type: Object,
      default: null
    },
    size: {
      type: String,
      default: () => config.default.size,
      validator(value) {
        return Object.keys(config.border.size.horizontal).includes(value) || Object.keys(config.border.size.vertical).includes(value);
      }
    },
    orientation: {
      type: String,
      default: "horizontal",
      validator: (value) => ["horizontal", "vertical"].includes(value)
    },
    type: {
      type: String,
      default: () => config.default.type,
      validator: (value) => ["solid", "dotted", "dashed"].includes(value)
    },
    class: {
      type: [String, Object, Array],
      default: () => ""
    },
    ui: {
      type: Object,
      default: () => ({})
    }
  },
  setup(props) {
    const { ui, attrs } = useUI("divider", toRef(props, "ui"), config);
    const wrapperClass = computed(() => {
      return twMerge(twJoin(
        ui.value.wrapper.base,
        ui.value.wrapper[props.orientation]
      ), props.class);
    });
    const containerClass = computed(() => {
      return twJoin(
        ui.value.container.base,
        ui.value.container[props.orientation]
      );
    });
    const borderClass = computed(() => {
      return twJoin(
        ui.value.border.base,
        ui.value.border[props.orientation],
        ui.value.border.size[props.orientation][props.size],
        ui.value.border.type[props.type]
      );
    });
    return {
      // eslint-disable-next-line vue/no-dupe-keys
      ui,
      attrs,
      wrapperClass,
      containerClass,
      borderClass
    };
  }
});
</script>
<template>
  <UModal v-model="isOpen" prevent-close :ui="{ width: 'sm:max-w-2xl' }">
    <UCard :ui="{ divide: 'divide-y divide-gray-100 dark:divide-gray-800' }">
      <template #header>
        <div class="flex items-center justify-between">
          <h3 class="text-base font-semibold leading-6 text-gray-900 dark:text-white">
            Configuraes da Coleo
          </h3>
          <UButton color="gray" variant="ghost" icon="i-heroicons-x-mark" class="-my-1" @click="isOpen = false" />
        </div>
      </template>

      <div class="space-y-6 max-h-[70vh] overflow-y-auto p-1">
        <!-- Basic Info -->
        <div class="space-y-4">
          <UFormGroup label="Nome da Coleo" required>
            <UInput v-model="form.name" placeholder="ex. Minha Coleo" />
          </UFormGroup>
          <UFormGroup label="Descrio">
            <UTextarea v-model="form.description" />
          </UFormGroup>
        </div>

        <UDivider />

        <!-- Visibility -->
        <CollectionAccessControl v-model="form.visibility" :collection-id="collectionId" />

        <UDivider />

        <!-- Fields Management -->
        <div class="space-y-4">
          <div class="flex items-center justify-between">
            <h4 class="text-sm font-bold uppercase tracking-wider text-gray-500">Campos Personalizados</h4>
            <UButton size="xs" icon="i-heroicons-plus" @click="addField">Adicionar Campo</UButton>
          </div>

          <div v-if="form.fields.length === 0" class="text-center py-4 bg-gray-50 dark:bg-gray-800/50 rounded-lg border-2 border-dashed border-gray-200 dark:border-gray-700">
             <p class="text-sm text-gray-500">Nenhum campo personalizado ainda.</p>
          </div>

          <div class="space-y-3">
            <div v-for="(field, index) in form.fields" :key="field.id || index" class="relative group">
              <FieldDefinition v-model="form.fields[index]" @remove="removeField(index)" />
            </div>
          </div>
        </div>
      </div>

      <template #footer>
        <div class="flex justify-end gap-3">
          <UButton color="gray" variant="ghost" @click="isOpen = false">Cancelar</UButton>
          <UButton color="primary" :loading="loading" @click="saveChanges">Salvar Alteraes</UButton>
        </div>
      </template>
    </UCard>
  </UModal>
</template>

<script setup lang="ts">
const props = defineProps<{
  modelValue: boolean
  collectionId: string
}>()

const emit = defineEmits(['update:modelValue', 'refresh'])

const isOpen = computed({
  get: () => props.modelValue,
  set: (val) => emit('update:modelValue', val)
})

const toast = useToast()
const loading = ref(false)

const form = reactive({
  name: '',
  description: '',
  visibility: 'private' as 'private' | 'shared' | 'public',
  fields: [] as any[]
})

// Load current data when opening
watch(isOpen, async (val) => {
  if (val) {
    loading.value = true
    try {
      const data = await $fetch(`/api/collections/${props.collectionId}`)
      
      form.name = (data as any).name
      form.description = (data as any).description
      form.visibility = (data as any).visibility || 'private'
      form.fields = ((data as any).fields || []).sort((a: any, b: any) => a.folder_order - b.folder_order)
    } catch (e: any) {
      toast.add({ title: 'Erro ao carregar', description: e.message, color: 'red' })
      isOpen.value = false
    } finally {
      loading.value = false
    }
  }
})

const addField = () => {
  form.fields.push({
    name: '',
    type: 'text',
    options: [],
    visible: true,
    required: false,
    folder_order: form.fields.length
  })
}

const removeField = async (index: number) => {
  const field = form.fields[index]
  if (field.id) {
    if (!confirm(`Excluir campo "${field.name}"? Todos os dados deste campo sero perdidos!`)) return
    
    try {
        await $fetch(`/api/collections/${props.collectionId}/fields/${field.id}`, { method: 'DELETE' })
        toast.add({ title: 'Campo removido', color: 'green' })
    } catch (e: any) {
        toast.add({ title: 'Erro', description: e.message, color: 'red' })
        return
    }
  }
  form.fields.splice(index, 1)
}

const saveChanges = async () => {
  loading.value = true
  try {
    await $fetch(`/api/collections/${props.collectionId}`, {
      method: 'PATCH',
      body: {
        name: form.name,
        description: form.description,
        visibility: form.visibility,
        fields: form.fields.map((f, index) => ({ ...f, folder_order: index }))
      }
    })

    toast.add({ title: 'Sucesso', description: 'Configuraes atualizadas', color: 'green' })
    emit('refresh')
    isOpen.value = false
  } catch (e: any) {
    toast.add({ title: 'Erro ao salvar', description: e.message, color: 'red' })
  } finally {
    loading.value = false
  }
}
</script>
<template>
  <div class="mt-8">
    <h3 class="text-xl font-bold mb-4">Comentrios</h3>
    
    <!-- Add Comment -->
    <div class="mb-6 flex gap-3" v-if="user">
      <UAvatar :src="(user as any).avatar" :alt="(user as any).name" />
      <div class="flex-1 space-y-2">
        <UTextarea v-model="newComment" placeholder="Escreva um comentrio..." :rows="2" autoresize />
        <div class="flex justify-end">
          <UButton size="sm" :loading="posting" :disabled="!newComment.trim()" @click="postComment">Comentar</UButton>
        </div>
      </div>
    </div>

    <!-- List -->
    <div class="space-y-4">
      <div v-if="comments.length === 0" class="text-center text-gray-500 py-4">
        Nenhum comentrio ainda. Seja o primeiro!
      </div>
      
      <div v-else v-for="comment in comments" :key="(comment as any).id" class="flex gap-3">
        <UAvatar :src="(comment as any).avatar_url" :alt="(comment as any).full_name" size="sm" />
        <div class="flex-1">
          <div class="bg-gray-50 dark:bg-gray-800 rounded-lg p-3">
            <div class="flex justify-between items-start mb-1">
              <span class="font-semibold text-sm">{{ (comment as any).full_name || 'Annimo' }}</span>
              <span class="text-xs text-gray-500">{{ new Date((comment as any).created_at).toLocaleDateString() }}</span>
            </div>
            <p class="text-sm text-gray-700 dark:text-gray-300 whitespace-pre-wrap">{{ (comment as any).content }}</p>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script setup lang="ts">
const props = defineProps<{
  collectionId: string
}>()

const { user } = useUserSession()
const toast = useToast()

const comments = ref<any[]>([])
const newComment = ref('')
const posting = ref(false)

const loadComments = async () => {
  try {
    const data = await $fetch(`/api/collections/${props.collectionId}/comments`)
    comments.value = (data as any)
  } catch (e) {
    console.error('Error loading comments:', e)
  }
}

const postComment = async () => {
  if (!newComment.value.trim() || !user.value) return
  
  posting.value = true
  try {
    await $fetch(`/api/collections/${props.collectionId}/comments`, {
      method: 'POST',
      body: {
        content: newComment.value.trim()
      }
    })
    
    newComment.value = ''
    loadComments()
    
  } catch (error: any) {
    toast.add({ title: 'Erro', description: error.message, color: 'red' })
  } finally {
    posting.value = false
  }
}

onMounted(loadComments)
</script>
<template>
  <div class="p-4">
    <!-- Header -->
    <div class="mb-6 flex flex-col md:flex-row justify-between md:items-center gap-4">
      <div>
        <div class="flex items-center gap-2 mb-1">
           <UButton to="/dashboard" icon="i-heroicons-arrow-left" variant="ghost" color="gray" size="sm" />
           <h1 class="text-2xl font-bold">{{ collection?.name || 'Carregando...' }}</h1>
        </div>
        <p class="text-gray-500 text-sm ml-9">{{ collection?.description }}</p>
      </div>
      <div class="flex gap-2">
        <UButton 
          v-if="selectedItems.length > 0" 
          color="red" 
          variant="soft" 
          icon="i-heroicons-trash" 
          @click="deleteSelectedItems"
        >
          Excluir ({{ selectedItems.length }})
        </UButton>
        <UButton variant="outline" icon="i-heroicons-arrow-up-tray" @click="isOpenImport = true">Importar Excel</UButton>
        <UButton icon="i-heroicons-plus" @click="openCreateModal">Novo Item</UButton>
        <UTooltip text="Configuraes e Campos">
          <UButton 
            icon="i-heroicons-cog-6-tooth" 
            variant="ghost" 
            color="gray" 
            @click="isOpenSettings = true"
          />
        </UTooltip>
      </div>
    </div>

    <!-- Stats Section Header -->
    <div v-if="fields.length > 0 && items.length > 0" class="mb-4 flex items-center justify-between bg-white/50 dark:bg-gray-900/50 backdrop-blur-sm p-2 rounded-lg border border-gray-100 dark:border-gray-800">
      <div class="flex items-center gap-2 ml-2">
         <div class="p-1 bg-primary-100 dark:bg-primary-900/40 rounded">
            <UIcon name="i-heroicons-presentation-chart-line" class="w-4 h-4 text-primary-600 dark:text-primary-400" />
         </div>
         <h2 class="text-sm font-bold uppercase tracking-wider text-gray-700 dark:text-gray-300">Resumo da Coleo</h2>
      </div>
      <UButton 
        :icon="showStats ? 'i-heroicons-eye-slash' : 'i-heroicons-eye'" 
        variant="ghost" 
        color="gray" 
        size="xs"
        @click="toggleStats"
      >
        {{ showStats ? 'Ocultar Dashboards' : 'Mostrar Dashboards' }}
      </UButton>
    </div>

    <!-- Stats Dashboard -->
    <Transition
      enter-active-class="transition duration-300 ease-out"
      enter-from-class="transform -translate-y-4 opacity-0"
      enter-to-class="transform translate-y-0 opacity-100"
      leave-active-class="transition duration-200 ease-in"
      leave-from-class="transform translate-y-0 opacity-100"
      leave-to-class="transform -translate-y-4 opacity-0"
    >
      <CollectionStats v-if="showStats" :fields="fields" :items="rows" />
    </Transition>

    <!-- Filters/Search -->
    <div class="mb-4 flex flex-col md:flex-row gap-2">
      <UInput 
        v-model="searchQuery" 
        icon="i-heroicons-magnifying-glass" 
        placeholder="Buscar em todas as colunas..." 
        class="w-full md:w-64" 
        :ui="{ icon: { trailing: { pointer: '' } } }"
      >
        <template #trailing v-if="searchQuery || activeFiltersCount > 0">
           <UBadge size="xs" color="gray" variant="soft">{{ filteredRows.length }}</UBadge>
        </template>
      </UInput>
      
      <UPopover>
        <UButton color="white" icon="i-heroicons-funnel" trailing-icon="i-heroicons-chevron-down">
           Filtros {{ activeFiltersCount > 0 ? `(${activeFiltersCount})` : '' }}
        </UButton>
        <template #panel>
          <div class="p-4 w-80 space-y-4">
            <h4 class="font-bold text-sm">Filtros Ativos</h4>
            <div v-for="field in fields" :key="field.id" class="flex flex-col gap-1">
               <label class="text-xs font-medium text-gray-500">{{ field.name }}</label>
               <UInput 
                  v-model="filters[field.id]" 
                  size="xs" 
                  :placeholder="`Filtrar por ${field.name}`" 
               />
            </div>
            <UButton v-if="activeFiltersCount > 0" size="xs" color="gray" variant="ghost" block @click="clearFilters">Limpar Todos</UButton>
          </div>
        </template>
      </UPopover>
    </div>

    <!-- Table -->
    <UTable
      v-model="selectedItems"
      :rows="filteredRows"
      :columns="tableColumns"
      :loading="pending"
      class="w-full"
    >
      <template #actions-data="{ row }">
        <div class="flex gap-1 justify-end">
          <UButton icon="i-heroicons-pencil-square" size="xs" color="gray" variant="ghost" @click="openEditModal(row)" />
          <UButton icon="i-heroicons-trash" size="xs" color="red" variant="ghost" @click="deleteItem(row.id)" />
        </div>
      </template>

      <!-- Custom Cell Rendering for Multiselect/Boolean/Date -->
      <template v-for="col in dynamicColumns" :key="col.key" #[`${col.key}-data`]="{ row }">
         <span v-if="col.type === 'boolean'">
            <UBadge :color="row[col.key] ? 'green' : 'gray'" variant="soft">{{ row[col.key] ? 'Sim' : 'No' }}</UBadge>
         </span>
         <span v-else-if="col.type === 'multiselect'">
            {{ Array.isArray(row[col.key]) ? row[col.key].join(', ') : row[col.key] }}
         </span>
         <span v-else>
            {{ row[col.key] }}
         </span>
      </template>
    </UTable>

    <!-- Create/Edit Modal -->
    <UModal v-model="isOpen">
      <UCard>
        <template #header>
          <div class="font-bold">{{ editingItemId ? 'Editar Item' : 'Novo Item' }}</div>
        </template>

        <ItemForm
          v-if="fields.length > 0"
          :fields="fields"
          :initial-data="editingItemData"
          :loading="saving"
          :collection-id="collectionId"
          @save="handleSave"
          @cancel="isOpen = false"
        />
      </UCard>
    </UModal>
    
     <!-- Import Modal -->
    <UModal v-model="isOpenImport">
       <UCard>
         <template #header>Importar do Excel</template>
         <ImportWizard 
            v-if="isOpenImport" 
            :collection-id="collectionId" 
            :collection-fields="fields" 
            @close="isOpenImport = false"
            @refresh="loadData"
         />
       </UCard>
    </UModal>

    <!-- Collection Settings Modal -->
    <CollectionSettingsModal 
      v-model="isOpenSettings" 
      :collection-id="collectionId" 
      @refresh="loadData"
    />

    <!-- Comments -->
    <CommentSection :collection-id="collectionId" />

  </div>
</template>

<script setup lang="ts">
const route = useRoute()
const collectionId = route.params.id as string
const toast = useToast()

const collection = ref<any>(null)
const fields = ref<any[]>([])
const items = ref<any[]>([])
const selectedItems = ref<any[]>([])

// Modal State
const isOpen = ref(false)
const isOpenImport = ref(false)
const isOpenSettings = ref(false)
const showStats = ref(true)

const toggleStats = () => {
  showStats.value = !showStats.value
  localStorage.setItem('show-stats', String(showStats.value))
}
const editingItemId = ref<string | null>(null)
const editingItemData = ref<any>({})
const saving = ref(false)

// Filter State
const searchQuery = ref('')
const filters = ref<Record<string, string>>({})

const activeFiltersCount = computed(() => {
  return Object.values(filters.value).filter(val => val && val.trim() !== '').length
})

const clearFilters = () => {
  filters.value = {}
}

// Load Data
const { data: collectionData, pending: pendingCollection, refresh: refreshCollection } = await useFetch(`/api/collections/${collectionId}`)
const { data: itemsData, pending: pendingItems, refresh: refreshItems } = await useFetch(`/api/collections/${collectionId}/items`)

watchEffect(() => {
  if (collectionData.value) {
    collection.value = collectionData.value
    fields.value = ((collectionData.value as any).fields || []).sort((a: any, b: any) => a.folder_order - b.folder_order)
  }
  if (itemsData.value) {
    items.value = (itemsData.value as any)
  }
})

const pending = computed(() => pendingCollection.value || pendingItems.value)

const loadData = () => {
  refreshCollection()
  refreshItems()
}

onMounted(() => {
  const saved = localStorage.getItem('show-stats')
  if (saved !== null) {
    showStats.value = saved === 'true'
  }
})

// Computed Table Rows (Pivot)
// items.value is already pivoted from the API
const rows = computed(() => items.value)

const filteredRows = computed(() => {
  let result = rows.value

  // 1. Search Query
  if (searchQuery.value) {
    const q = searchQuery.value.toLowerCase()
    result = result.filter(row => {
      // Search visible fields mainly
      return Object.values(row).some(val => String(val).toLowerCase().includes(q))
    })
  }

  // 2. Column Filters
  for (const [fieldId, filterValue] of Object.entries(filters.value)) {
    if (filterValue && filterValue.trim() !== '') {
       const q = filterValue.toLowerCase()
       result = result.filter(row => {
          const val = row[fieldId]
          return String(val ?? '').toLowerCase().includes(q)
       })
    }
  }

  return result
})

// Dynamic Columns
const dynamicColumns = computed(() => {
  return fields.value.filter(f => f.visible).map(f => ({
    key: f.id,
    label: f.name,
    sortable: true,
    type: f.type
  }))
})

const tableColumns = computed(() => {
  return [
    ...dynamicColumns.value,
    { key: 'actions', label: 'Aes' }
  ]
})

// Modal Actions
const openCreateModal = () => {
  editingItemId.value = null
  editingItemData.value = {}
  isOpen.value = true
}

const openEditModal = (row: any) => {
  editingItemId.value = row.id
  editingItemData.value = { ...row }
  isOpen.value = true
}

// Actions
const handleSave = async (formData: any) => {
  try {
    saving.value = true
    
    await $fetch(`/api/collections/${collectionId}/items`, {
      method: 'POST',
      body: {
        id: editingItemId.value,
        values: formData
      }
    })

    toast.add({ title: 'Sucesso', description: 'Item salvo', color: 'green' })
    isOpen.value = false
    loadData()

  } catch (error: any) {
    toast.add({ title: 'Erro', description: error.message, color: 'red' })
  } finally {
    saving.value = false
  }
}

const deleteItem = async (id: string) => {
  if (!confirm('Excluir item?')) return
  try {
    await $fetch(`/api/collections/${collectionId}/items/${id}`, { method: 'DELETE' })
    toast.add({ title: 'Excludo', color: 'green' })
    loadData()
  } catch (e: any) {
    toast.add({ title: 'Erro', description: e.message, color: 'red' })
  }
}

const deleteSelectedItems = async () => {
  const count = selectedItems.value.length
  if (count === 0) return
  if (!confirm(`Tem certeza que deseja excluir ${count} itens?`)) return

  const ids = selectedItems.value.map(i => i.id)
  
  try {
    // We could make a bulk delete API or call multiple deletes
    // For now, let's assume we have a bulk endpoint or just call in a loop (not ideal but works)
    await Promise.all(ids.map(id => $fetch(`/api/collections/${collectionId}/items/${id}`, { method: 'DELETE' })))
    toast.add({ title: 'Excludo', description: `${count} itens excludos`, color: 'green' })
    selectedItems.value = []
    loadData()
  } catch (e: any) {
    toast.add({ title: 'Erro', description: e.message, color: 'red' })
  }
}
</script>
